import Intact from 'intact';
import template from './index.vdt';

const path = require('path');

module.exports = {
    ...
    resolve: {
        alias: {
            // 让kpc组件指向多文件构建版本，可以统一组件加载路径
            'kpc': path.resolve(__dirname, './node_modules/kpc/@css')
        }
    },
    module: {
        rules: [
            {
                test: /\.css$/,
                use: [
                    {
                        loader: 'style-loader',
                    },
                    {
                        loader: 'css-loader',
                        options: {
                            url: true
                        }
                    }
                ]
            },
            // 引入字体文件
            {
                test: /\.(woff2?|eot|ttf|otf|svg)(\?.*)?$/,
                use: [
                    {
                        loader: 'file-loader',
                        options: {
                            outputPath: './fonts/',
                        }
                    }
                ]
            },
        ]
    }
}

